mapper.delete:
delete from dept_manager
where emp_no = #{empNo,jdbcType=INTEGER}
and dept_no = #{deptNo,jdbcType=CHAR}

mapper.update:
update dept_manager
set from_date = #{fromDate,jdbcType=DATE},
to_date = #{toDate,jdbcType=DATE}
where emp_no = #{empNo,jdbcType=INTEGER}
and dept_no = #{deptNo,jdbcType=CHAR}

mapper.select:
select emp_no, dept_no, from_date, to_date
from dept_manager
where emp_no = #{empNo,jdbcType=INTEGER}
and dept_no = #{deptNo,jdbcType=CHAR}
